mapper.delete:
delete from emp where id in

    #{id}
    -- ids = [] ,id will select the each element in the ids then to concat to the style with (1,2,3)

mapper.select:
select *
from emp


        name like concat('%', #{name}, '%')


        and gender = #{gender}


        and entrydate between #{begin} and #{end}


order by update_time desc
